// Funkcja konwertująca tekst do GSM-7
function toGSM7(text) {
    if (!text) return '';

    // 1. Usuń znaki diakrytyczne z liter łacińskich
    let normalized = text.normalize('NFD').replace(/[\u0300-\u036f]/g, '');

    // 2. Mapa zamiany znaków spoza GSM-7 na zbliżone
    const charMap = {
        '’': "'", '‘': "'", '“': '"', '”': '"', '–': '-', '—': '-',
        'à': 'a', 'á': 'a', 'â': 'a', 'ä': 'a', 'ã': 'a', 'å': 'a',
        'ç': 'c', 'è': 'e', 'é': 'e', 'ê': 'e', 'ë': 'e',
        'ì': 'i', 'í': 'i', 'î': 'i', 'ï': 'i',
        'ñ': 'n', 'ò': 'o', 'ó': 'o', 'ô': 'o', 'ö': 'o', 'õ': 'o',
        'ù': 'u', 'ú': 'u', 'û': 'u', 'ü': 'u',
        'ý': 'y', 'ÿ': 'y',
        'ß': 'ss', 'Œ': 'OE', 'œ': 'oe', 'Æ': 'AE', 'æ': 'ae',
        'ł': 'l', 'Ł': 'L', 'Ø': 'O', 'ø': 'o', 'Đ': 'D', 'đ': 'd'
    };

    normalized = normalized.split('').map(c => charMap[c] || c).join('');

    // 3. Definicja znaków dopuszczalnych w GSM-7
    const gsm7Chars =
        '@£$¥èéùìòÇ\nØø\rÅåΔ_ΦΓΛΩΠΨΣΘΞ ' +
        "abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ" +
        "0123456789" +
        "!\"#¤%&'()*+,-./:;<=>?¡ÄÖÑÜ§¿äöñü";

    // 4. Usuń znaki niezgodne z GSM-7
    return normalized.split('').filter(c => gsm7Chars.includes(c)).join('');
}

// Limit SMS
