// Funkcja wykrywająca czy tekst zawiera znaki spoza alfabetu łacińskiego
function isNonLatin(text) {
    // Znaki spoza zakresu łacińskiego
    const nonLatinRegex = /[^\u0000-\u007F\u00A0-\u024F]/;
    return nonLatinRegex.test(text);
}

// Funkcja konwertująca tekst do GSM-7
function toGSM7(text) {
    if (!text) return '';

    // 1. Usuń diakrytyki
    let normalized = text.normalize('NFD').replace(/[\u0300-\u036f]/g, '');

    // 2. Mapa zamian
    const charMap = {
        '’': "'", '‘': "'", '“': '"', '”': '"', '–': '-', '—': '-',
        'ß': 'ss', 'Œ': 'OE', 'œ': 'oe', 'Æ': 'AE', 'ae': 'ae',
        'ł': 'l', 'Ł': 'L', 'Ø': 'O', 'ø': 'o', 'Đ': 'D', 'đ': 'd'
    };

    normalized = normalized.split('').map(c => charMap[c] || c).join('');

    // 3. Dozwolone znaki GSM-7
    const gsm7Chars =
        '@£$¥èéùìòÇ\nØø\rÅåΔ_ΦΓΛΩΠΨΣΘΞ ' +
        "abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ" +
        "0123456789" +
        "!\"#¤%&'()*+,-./:;<=>?¡ÄÖÑÜ§¿äöñü";

    // 4. Usuń niedozwolone
    return normalized.split('').filter(c => gsm7Chars.includes(c)).join('');
}

// Limity SMS
const GSM_LIMIT = 160;
const UCS2_LIMIT = 70;
const ELLIPSIS = "...";
const ELLIPSIS_LENGTH = ELLIPSIS.length;


// Przetwarzanie itemów
for (const item of items) {
    const input = item.json;

    const smsPartText = $('SMS part 1').first().json.sms_firstPart || '';
    const itemText = input.output?.[0]?.content?.[0]?.text || '';

    const rawMessage = (smsPartText + itemText).trim();

    // WYKRYWANIE ALFABETU
    const nonLatin = isNonLatin(rawMessage);

    let finalSmsText;

    if (!nonLatin) {
        // ============================
        //   TRYB ŁACIŃSKI (GSM-7)
        // ============================
        const cleanMessage = toGSM7(rawMessage);

        if (cleanMessage.length > GSM_LIMIT) {
            finalSmsText =
                cleanMessage.substring(0, GSM_LIMIT - ELLIPSIS_LENGTH) + ELLIPSIS;
        } else {
            finalSmsText = cleanMessage;
        }

    } else {
        // ============================
        //  TRYB NIEŁACIŃSKI (UCS-2)
        // ============================

        if (rawMessage.length > UCS2_LIMIT) {
            finalSmsText =
                rawMessage.substring(0, UCS2_LIMIT - ELLIPSIS_LENGTH) + ELLIPSIS;
        } else {
            finalSmsText = rawMessage;
        }
    }

    // Zapis do item.json
    input.finalSmsText = finalSmsText;
    input.finalSmsLength = finalSmsText.length;
}

return items;

