const SMS_LIMIT = 155; //160

// Przetwarzanie każdego elementu w danych
for (const item of items) {
  const input = item.json;

  // 1. Definiowanie pól
  const name = $('Webhook').first().json.body.createdBy || '';


  // 2. Łączenie pól w format: "Imię Nazwisko; Tytuł; " (Z POLSKIMI ZNAKAMI)
  // Używamy separatorów "; " (dwa znaki)
  const firstPartRaw = `Knowshare: ${name}; `;

  // 3. Obliczenie DŁUGOŚCI tej części (na podstawie RAW stringu)
  const firstPartLength = firstPartRaw.length;

  // 4. Wyznaczenie różnicy (maksymalnego limitu dla Treści)
  const maxContentLength = SMS_LIMIT - firstPartLength;

  // Zapewnienie, że limit nie jest ujemny
  const finalContentLimit = Math.max(0, maxContentLength);

  // Dodanie nowych zmiennych do obiektu JSON:
  // - sms_firstPart: sformatowana część z polskimi znakami
  // - sms_contentLimit: obliczony limit znaków dla treści (dla Chat GPT)
  input.sms_firstPart = firstPartRaw;
  input.sms_contentLimit = finalContentLimit;
}

return items;

