// Pobierz surowy tekst JSON z OpenAI (pierwsze wejście)
let raw = $input.first().json.output[0].content[0].text || "";

// Pobranie oryginalnej wiadomości, która nie jest już generowana przez AI
const originalMessage = $('Webhook').first().json.body.message || ""; 

// 1. Oczyszczanie surowego tekstu
if (raw.includes("```")) {
    raw = raw.replace(/```json|```/g, "").trim();
}

// 2. Bezpieczne czyszczenie białych znaków
raw = raw.replace(/[\n\r\t\u00A0\uFEFF]/g, "").trim(); 

// 3. Konwersja apostrofów na cudzysłowy
raw = raw.replace(/'/g, "\"");

let parsed;

// Parsowanie JSON i obsługa błędów
try {
    parsed = JSON.parse(raw);
} catch (error) {
    // Struktura błędu z wstrzykniętą wiadomością oryginalną
    return [{
        json: {
            task_title: "",
            interpretation: "",
            recommendation: "",
            "original-message": originalMessage,
            task_description: "", 
            _error: "Błąd parsowania JSON: " + error.message + " | Czysty ciąg: " + raw.substring(0, 100) + "..."
        }
    }];
}

// 4. Transformacja do oczekiwanego płaskiego formatu
const obj = (Array.isArray(parsed) && parsed.length > 0) ? parsed[0] : parsed;
const taskDescriptionData = obj.task_description || {};

// Pobieranie wartości generowanych przez AI
const interpretation = taskDescriptionData.interpretation || "";
const recommendation = taskDescriptionData.recommendation || "";
const taskTitle = obj.task_title || "";

// 5. STWORZENIE NOWEGO POLA 'task_description' (podsumowanie)
const newTaskDescription = 
    `1: ${originalMessage}` +
    ` | 2: ${interpretation}` +
    ` | 3: ${recommendation}`;



// Zwrot finalnego, spójnego formatu
return [{
    json: {
        task_title: taskTitle,


        // Pola generowane przez AI
        interpretation: interpretation,
        recommendation: recommendation,
        // Pole wypełnione bezpośrednio z Webhooka
        //"original-message": originalMessage, 


        // Nowe, połączone pole
        task_description: newTaskDescription 
    }
}];

