const webhookData = $('Webhook').first().json || {};
const indexData = $('Set index').first().json || {};

// Zabezpieczony indeks załącznika
const index = Number(indexData.index) || 0; 

// Tablica załączników
const attachments = webhookData.body?.attachments || [];
const selectedAttachment = attachments[index] || null;

// Definicje rozszerzeń plików
const AUDIO_EXT = ['mp3','wav','m4a','flac','ogg','oga','webm','mp4','mpeg','mpga','aac'];
const VIDEO_EXT = ['mp4', 'mov', 'mpeg', 'mpg', 'webm', 'wmv', 'flv', '3gp', 'avi'];
const IMAGE_EXT = ['png', 'jpg', 'jpeg', 'gif', 'webp'];

// Klasyfikacja: 0 = unknown, 1 = image, 2 = audio (OpenAI), 3 = video (Gemini)
let type_ok = 0;
let fileName = null;

// Pobieranie nazwy pliku
if (typeof selectedAttachment === 'string') {
    fileName = selectedAttachment;
} else if (typeof selectedAttachment === 'object' && selectedAttachment?.filename) {
    fileName = selectedAttachment.filename;
}

// Logika klasyfikacji
if (fileName) {
    const normalized = fileName.toLowerCase(); 
    const ext = normalized.split('.').pop(); // Wyciągnij samo rozszerzenie

    if (ext) { // Sprawdź, czy rozszerzenie istnieje
        if (IMAGE_EXT.includes(ext)) {
            type_ok = 1; 
        } else if (AUDIO_EXT.includes(ext)) {
            type_ok = 2;
        } else if (VIDEO_EXT.includes(ext)) {
            type_ok = 3; 
        }
    }
}

return [
    {
        json: {
            attachment: selectedAttachment,
            type_ok: type_ok,
        }
    }
];

for (const item of items) {
  if (item.binary && item.binary.image) {
    item.binary.image.mimeType = 'image/jpeg';
  }
}
return items;

// Funkcja wykrywająca czy tekst zawiera znaki spoza alfabetu łacińskiego
function isNonLatin(text) {
    // Znaki spoza zakresu łacińskiego
    const nonLatinRegex = /[^\u0000-\u007F\u00A0-\u024F]/;
    return nonLatinRegex.test(text);
}

// Funkcja konwertująca tekst do GSM-7
function toGSM7(text) {
    if (!text) return '';

    // 1. Usuń diakrytyki
    let normalized = text.normalize('NFD').replace(/[\u0300-\u036f]/g, '');

    // 2. Mapa zamian
    const charMap = {
        '’': "'", '‘': "'", '“': '"', '”': '"', '–': '-', '—': '-',
        'ß': 'ss', 'Œ': 'OE', 'œ': 'oe', 'Æ': 'AE', 'ae': 'ae',
        'ł': 'l', 'Ł': 'L', 'Ø': 'O', 'ø': 'o', 'Đ': 'D', 'đ': 'd'
    };

    normalized = normalized.split('').map(c => charMap[c] || c).join('');

    // 3. Dozwolone znaki GSM-7
    const gsm7Chars =
        '@£$¥èéùìòÇ\nØø\rÅåΔ_ΦΓΛΩΠΨΣΘΞ ' +
        "abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ" +
        "0123456789" +
        "!\"#¤%&'()*+,-./:;<=>?¡ÄÖÑÜ§¿äöñü";

    // 4. Usuń niedozwolone
    return normalized.split('').filter(c => gsm7Chars.includes(c)).join('');
}

