// --- Webhook i Config
const webhook = $('Webhook').item.json;
const config = $('Config').item.json;

// --- Id zasobu z webhooka
const assetId = webhook?.body?.assetId;
const assetsDict = config?.setup?.assets || {};

let asset_name = '';
let trello_list_id = '';

// --- Szukaj po assetId
for (const key of Object.keys(assetsDict)) {
