			task_title: title,
			task_description: description,
			asset_name,
            trello_list_id
		}
	}
];

// Pobierz surowy tekst z pola "text"
let raw = $input.first().json.output[0].content[0].text || "";

// Sprawdź, czy tekst zawiera ```json i usuń jeśli tak
if (raw.includes("```json")) {
    raw = raw.replace(/```json\\n|```/g, '').replace(/\\n/g, '\n').trim();
} else {
    // usuń ewentualne nowe linie i białe znaki z końca
    raw = raw.trim();
}

// Spróbuj sparsować JSON
let parsed;
try {
    parsed = JSON.parse(raw);
} catch (error) {
    // jeśli JSON jest niepoprawny, zwróć puste pola
    parsed = {
        task_title: "",
        task_description: ""
    };
}

// Zwróć jako JSON dla kolejnych węzłów
return [{ json: parsed }];

// Funkcja konwertująca tekst do GSM-7
function toGSM7(text) {
    if (!text) return '';

    // 1. Usuń znaki diakrytyczne z liter łacińskich
    let normalized = text.normalize('NFD').replace(/[\u0300-\u036f]/g, '');

    // 2. Mapa zamiany znaków spoza GSM-7 na zbliżone
    const charMap = {
        '’': "'", '‘': "'", '“': '"', '”': '"', '–': '-', '—': '-',
        'à': 'a', 'á': 'a', 'â': 'a', 'ä': 'a', 'ã': 'a', 'å': 'a',
        'ç': 'c', 'è': 'e', 'é': 'e', 'ê': 'e', 'ë': 'e',
        'ì': 'i', 'í': 'i', 'î': 'i', 'ï': 'i',
        'ñ': 'n', 'ò': 'o', 'ó': 'o', 'ô': 'o', 'ö': 'o', 'õ': 'o',
        'ù': 'u', 'ú': 'u', 'û': 'u', 'ü': 'u',
        'ý': 'y', 'ÿ': 'y',
        'ß': 'ss', 'Œ': 'OE', 'œ': 'oe', 'Æ': 'AE', 'æ': 'ae',
        'ł': 'l', 'Ł': 'L', 'Ø': 'O', 'ø': 'o', 'Đ': 'D', 'đ': 'd'
    };

    normalized = normalized.split('').map(c => charMap[c] || c).join('');

    // 3. Definicja znaków dopuszczalnych w GSM-7
    const gsm7Chars =
        '@£$¥èéùìòÇ\nØø\rÅåΔ_ΦΓΛΩΠΨΣΘΞ ' +
        "abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ" +
        "0123456789" +
        "!\"#¤%&'()*+,-./:;<=>?¡ÄÖÑÜ§¿äöñü";

    // 4. Usuń znaki niezgodne z GSM-7
    return normalized.split('').filter(c => gsm7Chars.includes(c)).join('');
}

// Limit SMS
const SMS_LIMIT = 160;
const ELLIPSIS = '...';
const ELLIPSIS_LENGTH = ELLIPSIS.length;

// Przetwarzanie każdego elementu w danych
for (const item of items) {
    const input = item.json;

    // Bezpieczne pobranie wszystkich pól tekstowych
    const smsPartText = $('SMS part 1').first().json.sms_firstPart || '';
    const itemText = input.output?.[0]?.content?.[0]?.text || '';

    // Połączenie tekstów i trim
    const rawMessage = (smsPartText + itemText).trim();

    // Konwersja do GSM-7
    const cleanMessage = toGSM7(rawMessage);

    // Obcinanie do limitu 160 znaków z "..."
    let finalSmsText = cleanMessage;
    if (cleanMessage.length > SMS_LIMIT) {
        finalSmsText = cleanMessage.substring(0, SMS_LIMIT - ELLIPSIS_LENGTH) + ELLIPSIS;
    }

    // Dodanie wyników do JSON
    input.finalSmsText = finalSmsText;
    input.finalSmsLength = finalSmsText.length;
}

return items;

